INSERT INTO `items` (`id_podcast`, `title_audio`, `description_audio`, `audio`, `image_audio`, `pubdata_audio`, `duration_audio`, `id_item`) VALUES
(1, 'Масяня: принципы, Парфенов, патреон', '\nВ девятом выпуске подкаста анимационной студии Blaster \"Про мультфильмы!\" разговариваем с создателем Масяни Олегом Куваевым о его феерическом проекте, который длится вот уже восемнадцать лет!Внутри выпуска:— Почему в интернет сначала лезли только умные и любопытные, а теперь все подряд— Как Олег Куваев ругался за права на персонажа и спал в коробке из-под компьютера— Причем здесь НТВ— Зачем нарушать все законы анимации— Где искать деньги на собственный проектЖдем ваших комментариев и фидбек на blaster@blaster.studio', 'https://anchor.fm/s/daae468/podcast/play/8936021/https%3A%2F%2Fd3ctxlq1ktw2nl.cloudfront.net%2Fproduction%2F2019-11-11%2F37954061-44100-2-e2127e96c4a2a.mp3', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/2193034/2193034-1569576874205-950e04403d667.jpg', '2019-12-11 19:08:20', '00:30:45', 1),
(1, 'Все любят мистера Фримена', '\nВ восьмом выпуске подкаста анимацонной студии Blaster \"Про мультфильмы!\" создатели проекта Mr. Freeman Павел Мунтян и Владимир Пономарев раскрывают (почти) все секреты.И да — этот эпизод был героически записан удаленно, а Гена звучит из бочки.Внутри выпуска:— Почему все так любят пасхалки?— Павел объясняет на пальцах, что анимация — это дорого— Как Фримен родился из тубуса с мелочью— YouTube против чересчур свободного контента— Обсуждаем социальную важность Фримена!Пишите свои комментарии и фидбек на blaster@blaster.studio\n\n', 'https://anchor.fm/s/daae468/podcast/play/8425896/https%3A%2F%2Fd3ctxlq1ktw2nl.cloudfront.net%2Fproduction%2F2019-10-13%2F34033284-44100-2-152f1f204e28d.mp3', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/2193034/2193034-1569576874205-950e04403d667.jpg', '2019-11-13 19:10:35', '00:41:09', 2),
(1, 'Планета Ай: минкульт, немое кино, индусы!', 'В седьмом выпуске подкаста анимационной студии Blaster \"Про мультфильмы!\" мы общаемся с создателями \"Белки и Стрелки\" и \"Планеты Ай\". Кажется, этот выпуск больше всего рассказывает про технические детали работы над мультсериалом в России!\nОбсудили:\n— Что помогает индусам анимировать лучше?\n— Сколько нужно режиссеров, чтобы сделать проект?\n— Почему минкульт не дает денег молодым и амбициозным?\n— Как политическая ситуация влияет на прокат мультфильмов зарубежом?\n\nА также отличные новости: теперь вы сможете слушать новые выпуски \"Про мультфильмы\" ещё и ВКонтакте!\n\nПишите свои отзывы, вопросы и комментарии: blaster@blaster.studio\n\n', 'https://anchor.fm/s/daae468/podcast/play/7824850/https%3A%2F%2Fd3ctxlq1ktw2nl.cloudfront.net%2Fproduction%2F2019-9-30%2F31654184-44100-2-0ae4b13b3a347.mp3', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/2193034/2193034-1569576874205-950e04403d667.jpg', '2019-10-30 19:08:57', '00:24:22', 3),
(1, 'Новое Простоквашино — как это сделано и кому это нужно', 'В шестом эпизоде подкаста анимационной студии Blaster \"Про мультфильмы\" мы разговариваем с Аленой Оятьевой и Еленой Черновой — создателями проекта \"Новое Простоквашино\" студии Союзмультфильм.\n\n— За что все ругают новые серии?\n— Почему дети — это самые благодарные зрители?\n— В чём очарование Шарика?\n— Зачем нужно переосмыслять советскую мультипликацию? \n\nПишете свои комментарии на blaster@blaster.studio\n', 'https://anchor.fm/s/daae468/podcast/play/7314106/https%3A%2F%2Fd3ctxlq1ktw2nl.cloudfront.net%2Fproduction%2F2019-9-22%2F30204524-44100-2-7e756613c3f4f.mp3', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/2193034/2193034-1569576874205-950e04403d667.jpg', '2019-10-22 19:13:08', '00:21:38', 4),
(1, 'Малышарики: лицензирование, детские песни, гендер', 'В пятом эпизоде подкаста анимационной студии Blaster \"Про мультфильмы\" Егор едет в Петербург и знакомится с продюсером \"Малышариков\" Александрой Артемьевой.\nВ этом выпуске:\n— Что сложного в том, чтобы продавать товары со своими персонажами?\n— Как на мультипликацию влияют актуальные вопросы гендера?\n— Зачем родители просят сделать песни про прививки и горшок?\n— Ну почему все так любят \"Телепузиков\"?!\n\nПишите ваши отзывы на blaster@blaster.studio\n\n', 'https://anchor.fm/s/daae468/podcast/play/7160636/https%3A%2F%2Fd3ctxlq1ktw2nl.cloudfront.net%2Fproduction%2F2019-9-16%2F29478513-44100-2-348ad2e22373d.mp3', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/2193034/2193034-1569576874205-950e04403d667.jpg', '2019-10-16 19:09:03', '00:30:34', 5),
(1, 'Почему смешарики — круглые?', 'В четвертом эпизоде подкаста “Про мультфильмы” анимационной студии Blaster мы разговариваем с художественным руководителем “Смешариков” Анатолием Прохоровым.\nОбсудили вот что:\n— Почему нельзя разговаривать с детьми свысока?\n— Как телепузики завоевали детей?\n— Зачем маленьким детям смотреть рекламу?\n— Какая часть тела самая важная в профессии аниматора?\n\nПишите нам письма и свои комментарии на blaster@blaster.studio\n', 'https://anchor.fm/s/daae468/podcast/play/5771260/https%3A%2F%2Fd3ctxlq1ktw2nl.cloudfront.net%2Fproduction%2F2019-9-9%2F27300581-44100-2-ac6ff2d4d6a59.mp3', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/2193034/2193034-1569576874205-950e04403d667.jpg', '2019-10-09 19:14:51', '00:30:35', 6),
(1, 'Кто такие фиксики? Большой-большой секрет!', '\nТретий выпуск вашего любимого подкаста про мультфильмы «‎Про мультфильмы»‎!Разговариваем с Дарьей Моргуновой, которая уже больше десяти лет занимается «фиксиками».— «Фиксиков» купили более, чем в 90 странах мира. Что? Да!— Почему сценаристы — самые обидчивые люди?— Как мерять анимационные бюджеты в “Шрэках”?— Сколько секунд может нарисовать аниматор в день?Пишите ваши комментарии, мы их очень ждём:  mailto:blaster@blaster.studio - blaster@blaster.studio', 'https://anchor.fm/s/daae468/podcast/play/5062983/https%3A%2F%2Fd3ctxlq1ktw2nl.cloudfront.net%2Fproduction%2F2019-9-2%2F25718095-44100-2-bc778242c64b2.mp3', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/2193034/2193034-1569576874205-950e04403d667.jpg', '2019-10-02 19:12:02', '00:30:58', 7),
(1, 'Герои Энвелла: «предподростки», фанаты, геймеры', '\nВо втором выпуске подкаста мы разговариваем с создателями мультсериала “Герои Энвелла” — Антоном Ланшаковым и Сергеем Моисеевым.Вот, что обсудили:— Что нужно не забыть, когда создаешь свою анимационную вселенную?— Почему телевизор всё ещё важен для детской аудитории?— Что это за работа такая — смотреть мультики, играть в игры и получать за это деньги?— Зачем фанаты шипперят создателей сериала?— Как выглядит фестиваль анимации в Каннах?Пишите ваши комментарии, мы их очень ждём:  mailto:blaster@blaster.studio - blaster@blaster.studio\n\n', 'https://anchor.fm/s/daae468/podcast/play/4839525/https%3A%2F%2Fd3ctxlq1ktw2nl.cloudfront.net%2Fproduction%2F2019-8-25%2F24890566-44100-2-6524ddcb8e946.mp3', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/2193034/2193034-1569576874205-950e04403d667.jpg', '2019-09-25 19:07:30', '00:25:21', 8),
(1, 'Синий трактор едет к нам!', '\nУра, это первый выпуск подкаста «‎Про мультфильмы»‎!В нём мы разговариваем с создателями мультсериала “Синий трактор” — Артуром Днепровским и Артемом Колпаковым.В этом выпуске:— Как написать песню на миллиард просмотров?— Сколько стоит сделать музыкальный мультик?— Почему детская музыка — это искусство навсегда?— Как ютуб связан с экономикой страны?— Почему зона комфорта — это угроза для любого бизнеса?Будем ждать ваших комментариев здесь: blaster@blaster.studio\n\n', 'https://anchor.fm/s/daae468/podcast/play/4608825/https%3A%2F%2Fd3ctxlq1ktw2nl.cloudfront.net%2Fproduction%2F2019-8-11%2F23222609-44100-2-6f1e4ed7a56ff.mp3', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/2193034/2193034-1569576874205-950e04403d667.jpg', '2019-09-11 19:13:35', '00:35:52', 9),
(1, 'Трейлер!', '\nПривет!Мы — анимационная студия Blaster и это наш подкаст про мультфильмы. Он так и называется — «Про мультфильмы‎»‎!Почему Ёжик — самый крутой из Смешариков, как написать песню на миллиард просмотров, как устроен детский контент и что об этом думают те, кто всё это смотрит — дети.Пишите нам письма вот сюда: blaster@blaster.studio!\n\n', 'https://anchor.fm/s/daae468/podcast/play/4492660/https%3A%2F%2Fd3ctxlq1ktw2nl.cloudfront.net%2Fstaging%2F2019-8-4%2F22459583-44100-2-924234e8f28a2.m4a', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/2193034/2193034-1569576874205-950e04403d667.jpg', '2019-09-04 19:13:07', '00:00:53', 10),
