INSERT INTO `podcasts` (`title_podcast`, `description_podcast`, `url_image_podcast`, `author_podcast`, `id_podcast`) VALUES
('Про мультфильмы', '«‎Про мультфильмы»‎ — это подкаст анимационной студии Blaster про мультфильмы!\n\nВ этом подкасте мы рассказываем о том, как создаются современные русские мультфильмы, чем вдохновляются их создатели и сколько всё это стоит.\nПочему Ёжик — самый крутой из Смешариков, как написать песню на миллиард просмотров, как устроен детский контент и что об этом думают те, кто всё это смотрит — дети.\n\nБудем ждать ваших комментариев здесь: blaster@blaster.studio', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/2193034/2193034-1569576874205-950e04403d667.jpg', 'Blaster Studio', 1),
('Аэростатика', NULL, 'https://aerostatica.ru/assets/images/plogo.png?v=2.1', 'Aerostatica.RU', 2),
('Инфа 100%', 'Самые честные мужские разговоры на кухне 🥃', 'https://cdn.simplecast.com/images/d84c53e8-ab63-402c-86f6-5ff8ea2a09da/29ff5366-3e13-437f-9451-d68394ca2431/3000x3000/main2019.jpg?&amp;aid=rss_feed', 'Kamhorka Production', 3),
('Батенька, где текст?', 'Вместе с нами вы шлёпали секс-кукол. Ходили в православные клубы знакомств.  Работали в сибирском такси. Путешествовали в Обитель чёрного дьявола. Вместе с авторами «Батеньки» вы совершили тысячу путешествий в глубины реальности. Настало время узнать,  как это происходит на самом деле.  В подкасте «Батенька, где текст?» главный редактор самиздата  Григорий Туманов распекает авторов, они страдают, оправдываются и выдают всё, что обычно остаётся за кадром. Это сериал о производстве наших лонгридов — о том, как жизнь превращается в журналистику.\r\n\r\nМузыка — Федя Балашов', 'https://media.simplecast.com/podcast/image/4464/1521213281-artwork.jpg', 'Глаголев FM', 4),
('Отвратительные мужики', 'Это еженедельный подкаст с disgustingmen.com. Мы говорим о кинематографе, музыке, светской жизни, путешествиях и видеоиграх.', 'https://i.podster.fm/img/55004/crop/yes/size/default/55004-2.jpg', 'Disgusting Men', 5),
('Животные в Студии', '«Животные в Cтудии» — это подкаст, ведущие которого шутят и смеются, обсуждая странные новости, кинематограф, видеоигры, а также проблемы простых людей. 18+\r\n\r\nПоддержи Животных на Patreon и в качестве благодарности ты получишь ранний доступ к новым выпускам подкаста, полное отсутствие цензуры и, конечно же, возможность приобщиться к эксклюзивным спецвыпускам — https://www.patreon.com/animalsinthestudio', 'https://i.podster.fm/img/157208/crop/yes/size/default/157208-25.jpg', 'Animals in the Studio', 6),
('Перемотка', '«Перемотка» — подкаст, основанный на пленках из семейных архивов, которые прислали наши слушатели. Каждый эпизод — человеческая история, по-своему иллюстрирующая тот или иной период истории России. Временной диапазон охватывает весь XX век — от революции 1917 года (которую один из героев видел своими глазами!) до распада Советского Союза и первых лет постсоветской России. Второй сезон «Перемотки» выходит как совместный подкаст «Медузы» и «Арзамаса». Если у вас в семье сохранились аудиозаписи и вы готовы рассказать связанные с ними истории, пишите на podcast.peremotka@gmail.com.', 'https://meduza.io/image/attachments/images/003/898/005/squarelarge/wVQn77rBywTwDEH5lknxMw.png', 'Медуза/Meduza', 7),
('Ребята, мы потрахались', 'Подкаст об отношениях. Вопросы от слушателей подкаста и читателей BroDude.ru', 'https://mojomedia.ru/wp-content/uploads/2018/09/my-potrahalis.png', 'Команда MojoMedia', 8),
('Бес Cознания', 'Мы четыре подруги — Лиза, Саша, Лера и Гаяна — с привычкой обсуждать за воскресными бранчами, что происходит с нами и вокруг нас. Однажды мы дорефлексировались, теперь делаем подкаст. \n \nРаз в неделю говорим о том, что нас волнует: от запретов на упоминание секса в соцсетях до вопросов об устройстве мира. Будем делиться триггерными инфоповодами и инсайтами из личного опыта. \n \nТерзает ли вас мелкий бес сознания, стоит ли за спиной Мефистофель и шепчет в ушко сомнения, вы обратились по адресу. Не торопитесь вызывать экзорциста, послушайте нас, и убедитесь, что с вами всё в порядке.', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded_nologo/1293842/1293842-1545743025202-c4c6032da1a3a.jpg', 'Бес сознания', 9),
('Будь Брендом', 'Авторский подкаст «Будь Брендом»\r\n\r\nСерия интервью о бизнесе и Личном Бренде со знаковыми предпринимателями, общественными деятелями и экспертами.\r\n\r\nЦель подкаста — показать на примерах героев интервью, что Личные Качества и Ценности человека — это ресурсы, которые можно и нужно использовать, чтобы достигать целей в бизнесе и карьере.\r\n\r\nПодкаст создан, как для людей, стремящихся выйти на новую ступень в своём деле и ищущих инструмент, который поможет это сделать, так и для новичков, которые хотят добиться успеха в начинаниях.\r\n\r\nАвтор и ведущая подкаста — Мария Азарёнок, эксперт по личному бренду и нетворкингу, предприниматель, вдохновляющий спикер, бизнес-блогер с аудиторией влияния более 360 000 подписчиков, автор книги «Активируй свой персональный бренд» \r\nПодробнее о книге https://clck.ru/GLtdi\r\n  \r\nКомпания Марии AzarenokPRO помогла создать Личный Бренд уже больше чем 10000 человек и продолжает популяризировать идею Персонального Бренда как эффективного маркетингового инструмента.\r\n\r\nВидео версия подкаста на youtube канале AzarenokPRO -https://www.youtube.com/playlist?list=PLNVRrs1QFsxhmAhzDqCovhdK-LblgKKDF\r\n\r\n“Быть брендом — иметь выстроенный образ, наращивать влияние и охват на нужные вам аудитории и в итоге уметь создавать ту репутацию, которая будет работать на ваши бизнес- задачи.”\r\n\r\nХотите, стать героем интервью? Пишите на podcast@azarenokpro.com\r\n\r\nhttps://instagram.com/azarenokpro/ \r\nhttps://www.youtube.com/c/AzarenokPRO\r\nhttps://azarenokpro.com\r\nhttps://vk.com/azarenokpro\r\nhttps://facebook.com/azarenokpro \r\n', 'https://i.podster.fm/img/226135/crop/yes/size/default/226135-14.jpg', 'Мария Азаренок', 10),
('Берись и делай', '\"Берись и делай\" — это программа о том, как создать бизнес без стартового капитала, связей и опыта. Конкретные рекомендации к действиям и развитие предпринимательского мышления. Гости программы — молодые бизнесмены, которые на собственных примерах рассказывают о том, как они построили свои компании с нуля, что ими двигало, какие ошибки они допускали и что в итоге привело их к успеху.  Подкаст \"Берись и делай\" скачивают более 100 000 раз в месяц. Контакты для вопросов по рекламе и сотрудничеству: Viber и WhatsApp: +7 999 214 53 26 Электронная почта: avs@shokobox.ru', 'http://file2.podfm.ru/22/222/2228/22287/images/lentava_25686_1_35.jpg', 'PodFM.ru', 11),
('Батина Консоль', 'Подкаст «Батина Консоль» — для всех, кто любит видеоигры! Раз в две недели мы обсуждаем свежие анонсы и события в игровой индустрии, делимся мнениями об играх и полезной информацией об актуальных платформах, ценах и скидках на игры.\nВедущие: Владимир Плотников, Олег Алексеев, Тимофей Загребельный.\nПодкаст выходит в рамках проекта BeardyCast.com\n', 'https://assets.fireside.fm/file/fireside-images/podcasts/images/3/384d9560-c59a-473f-aaa1-894650662d65/cover.jpg?v=1', '#BeardyCast', 12),
('Веб-стандарты', 'Новости фронтенда за неделю в подкасте сообщества «Веб-стандарты».', 'https://web-standards.ru/podcast/cover.png', 'Ольга Алексашенко, Никита Дубко, Вадим Макеев, Мария Просвирнина, Алексей Симоненко', 13),
('Голос', 'Редактор Sports.ru Роман Мун разговаривает с самыми известными людьми российского спорта. Обсуждаем жизнь, карьеру, деньги, страхи и проблемы.', 'https://cdn.simplecast.com/images/1e8a1dad-1bc1-4484-b288-68968fc4c247/38dcd768-6d74-4d69-83a1-964205313e06/3000x3000/1539272880artwork.jpg?&amp;aid=rss_feed', 'Sports.ru', 14),
('Дикие утки', 'Совершенно разные истории от гостей', 'https://mojomedia.ru/wp-content/uploads/2018/09/dikie-utki-1.png', 'Команда MojoMedia', 15),
('Аниме-подкаст Донмай', 'Каждые две недели мы обсуждаем небольшую часть аниме-сезона, суммируя впечатления от выходящих мультфильмов. Посмотреть все онгоинги невозможно, обсуждать сезон посерийно скучно (да и незачем), поэтому мы просто делимся мыслями о заинтересовавших нас сериалах.', 'http://i1.sndcdn.com/avatars-000342082949-mfjhis-original.jpg', 'Donmai', 16),
('Денис выгуливает собаку', 'Podcast by Денис Чужой', 'http://i1.sndcdn.com/avatars-000529848303-0rc5wb-original.jpg', 'Денис Чужой', 17),
('180 градусов', '\"180 градусов\" - авторский подкаст о людях, которые не боятся перемен и преображают мир вокруг себя.\n\nАня Ковалёва и Костя Колосков берут интервью у тех, кто не побоялся изменить свою жизнь, профессию или образ мыслей. У каждого героя своя история и свои причины: кто-то пришел к изменениям по велению сердца, кто-то - по воле обстоятельств, а кто-то просто не может иначе.\n\nОбратная связь:\nTelegram: https://t.me/turn180degrees;\nInstagram: @anyakovaleva;\nEmail: 180@brainstorm.fm', 'https://d3t3ozftmdmh3i.cloudfront.net/production/podcast_uploaded/1064846/1064846-1537906799455-de74e8f6095d5.jpg', 'Откровенно о переменах', 18),
('АльтВолна', 'Podcast by АльтВолна', 'http://i1.sndcdn.com/avatars-000680149835-hs1d25-original.jpg', 'АльтВолна', 19),
('В предыдущих сериях', 'Подкаст КиноПоиска, в котором главный редактор Лиза Сурганова и автор канала «Запасаемся попкорном» Иван Филиппов обсуждают главные сериальные новинки и новости. \n\nЗвукорежиссер — Геннадий Финн, продюсер — Евгения Молодцова.', 'https://cdn.simplecast.com/images/d7d0fb05-8067-4c7b-a256-8a89939b36a7/046c196d-57cc-44ff-9e41-3ea969e36527/3000x3000/cover.jpg?&amp;aid=rss_feed', 'КиноПоиск', 20),
('Душевный подкаст', 'Что может быть лучше душевной беседы? Только душевная беседа двух рыжих чуваков. Каждое воскресенье Константин Тростенюк и Денис Карамышев экспертно анализируют всё, что достойно экспертного анализа.', 'https://i.podster.fm/img/154366/crop/yes/size/default/154366-1.jpg', 'Константин Тростенюк и Денис Карамышев', 21),
('Диалоги Открытой библиотеки', 'Ежемесячные диалоги с участием лучших русскоязычных спикеров на самые актуальные темы. От министра культуры до Юрия Дудя, от Жоры Крыжовникова до Александра Сокурова. Сделано в Санкт-Петербурге.', 'https://i.podster.fm/img/223974/crop/yes/size/default/223974-1.jpg', 'Открытая библиотека', 22),
('Базовый Блок: блокчейн без буллшита', 'Русскоязычный подкаст о блокчейн-технологиях. Telegram, VK, Twitter, Instagram: /basicblockradio', 'http://static.libsyn.com/p/assets/3/b/f/8/3bf83f1e3e28734a/basicblockradio_original.png', 'Сергей Тихомиров, Иван Иваницкий, Сергей Павлин, Александр Селезнев', 23),
('Две сорванные башни', 'Молодые «технологи» Руси рассуждают о политике, фильмах, истории, музыке, играх, но делают это несколько странно.', 'http://i1.sndcdn.com/avatars-000537788661-3puezg-original.jpg', 'Две сорванные башни', 24),
('Автомобильные новости', 'Этот подкаст создан на основе автожурнала Carwego. Каждую неделю мы выбираем самое интересное из нашей живой автомобильной ленты. Свежие новости, профессиональные статьи, обзоры автоблогеров и мнение любителей в удобном для прослушивания в вашем автомобиле формате.', 'https://d3t3ozftmdmh3i.cloudfront.net/staging/podcast_uploaded_nologo/2860728/e8218fee262bc299.jpeg', 'Carwego.ru', 25),
('Прорыв', 'Подкаст для амбициозных творческих людей от режиссёра из Лондона Анны Радченко.\r\nВ каждом выпуске - откровенные разговоры про карьеру, творческий путь, деньги, поиск себя и всё, что нас всех так волнует, но часто не с кем обсудить. Гости подкаста - талантливые и успешные представители самых разных творческих профессий, от фотографов до фэшн иллюстраторов. Вместе с Анной они ищут ответы на важные вопросы.', 'https://cdn.simplecast.com/images/7a335c9c-9ca6-4b68-82ef-159df4f5f181/6e7ee254-84a1-4102-be00-ecefd0355045/3000x3000/1549200594artwork.jpg?&amp;aid=rss_feed', 'Анна Радченко', 26),
('Красная горка', 'Она старается, готовится. Мы приходим и всё портим. Она - филолог, мы - два «раздолбая». Три друга - срез культуры, воспитания и образования. И чтобы мы не говорили в начале эпизода, всегда есть гость, который всё разложит по «полочкам»', 'http://i1.sndcdn.com/avatars-000647065791-8j53j5-original.jpg', 'Krasnaya Gorka', 27),
('В бесконечность и далее', 'Два действующих IT менеджера ведут диалоги о высоких технологиях, буднях IT-индустрии и управления проектами, а также о выборе кофеварок, мотивации и перспективах футурологии как науки. Мы садимся и начинаем обсуждать интересные темы. Обычно это что-то технологическое, IT-шное или философско-прикладное, но мы не ограничены какой-либо областью, мы обсуждаем всё!', 'http://static.libsyn.com/p/assets/7/0/c/5/70c583da54a6eff5/logo.jpg', 'В бесконечность и далее', 28),
('Война - это мир', 'Еженедельный подкаст о ситуации на международной арене.\n\nВедущие: авторы телеграм-каналов \"Президент Трамп\", \"Вестминстер\" и \"Отчисленный МГИМОшник\".', 'https://images.podigee.com/0x,sQ5Z1w9rkLmVWNipjUZbPYco73T2eDcwghrQxpgKLUwU=/https://cdn.podigee.com/uploads/u3335/ea003693-b1c5-4b41-b80f-dd916f154b11.png', 'Malek, Vasily  Dmitry', 29),
('\n      Гараж (звук)  | Эхо Москвы\n    ', '\n      Гараж (звук)  на Эхе Москвы.\n        Ведущие:\n        Сергей Асланян\n    ', 'https://echo.msk.ru/att/element-13083-picture-165.gif', '', 30),
('Давай Поговорим', 'Это подкаст, где мы, Стелла Васильева и Анна Марчук, обсуждаем разные жизненные вопросы, делимся опытом и полезной информацией.', 'https://pogovorim.stellav.ru/wp-content/uploads/2018/11/Davai-pogovorim-logo-fullrez.jpg', 'Стелла Васильева, Анна Марчук', 31),
('Кристина, добрый день!', 'Привет! Меня зовут Кристина Вазовски. Каждую неделю я приглашаю в гости создателей моих любимых русскоязычных подкастов, расспрашиваю их про то, как они начинали и как устроен их подкастерский быт. А ещё собираю рекомендации — на какие подкасты нужно подписаться прямо сейчас.\n\nКороче, это подкаст о подкастах. \n\nНаш подкаст на каникулах до середины января!\n\nОбратная связь:\nTelegram-канал @provalpodcast\nInstagram @krisvazovsky \nПочта provalpodcast@gmail.com', 'https://cdn.simplecast.com/images/75a50f1b-08a3-435c-906f-71a54adf7aa8/8e1d00dd-d8fc-4c82-ab4f-88be085e5491/3000x3000/1547938938artwork.jpg?&amp;aid=rss_feed', 'Кристина Вазовски', 32),
('Дело случая', 'Редактор «Медузы» Екатерина Кронгауз и главный редактор «Постнауки» Андрей Бабицкий на конкретных примерах изучают, как меняется этика в современном мире — и где теперь пролегают границы дозволенного', 'https://meduza.io/image/attachments/images/002/487/630/squarelarge/boq4CMUk8hKDbVIJiwtk1g.png', 'Медуза / Meduza', 33),
('Дикий рынок', 'Привет! Мы Вера и Наташа и это наш подкаст «Дикий рынок». Когда-то вместе учились на пиарщиков, а теперь часто общаемся на профессиональные темы.\n\nКогда варишься в профессии больше 7 лет, контент про лайфхаки и рекламные приемы становится скучным и очевидным. Намного интереснее обсуждать, как маркетинг влияет на нашу повседневную жизнь.\n___\n\nЕсли у вас есть комментарии, предложения, вопросы, напишите нам. Можно прислать голосовое сообщение или написать нам в Телеграме (@dikijrynok)\n\nНе забудьте оставить комментарии и оценки, очень ждём обратную связь.', 'https://d3t3ozftmdmh3i.cloudfront.net/staging/podcast_uploaded_nologo/2151460/2151460-1565779031893-9a8cd5b4e3dcd.jpg', 'Дикий рынок', 34),
('Дизайнер', '«Дизайнер!» — мы сразу представляем человека, который рисует красивые картинки. Но за последние пару лет мир немного изменился — картинки ради картинок больше не работают, к тому же искусственный интеллект теперь тоже неплохо рисует. Этот подкаст о новой роли дизайнера, о том как творчество помогает бизнесу и меняет мир.', 'https://d3t3ozftmdmh3i.cloudfront.net/staging/podcast_uploaded_nologo/2871443/554492c1603d94eb.png', 'antomal.com™', 35),
('Жизнь человека', 'Подкаст о жизни хирурга-онколога, который обнаружил у себя рак.\n\nСайт проекта: https://cancer.takiedela.ru\nКанал в Telegram: http://t-do.ru/lifeofman', 'http://i1.sndcdn.com/avatars-000497138718-ikg2ft-original.jpg', 'Такие Дела', 36),
('За столом Райана Гослинга', 'Кто мы? Подкаст-шоу любителей собраться за столом. Обсуждаем самые актуальные и важные темы для нас. \nТри ведущих - четыре мнения - пять поводов послушать. \n- уникальные шутки;\n- море крутых композиций;\n- только самые интересные новости;\n- без цензуры;\n- узнай первым;\n- и стол самого Райана Гослинга!\nНаша команда подарит тебе заряд хорошего настроения на недели!\nЗа столом Райана Гослинга для Вас всегда найдется место =3\n\n#ЗаСтоломРайнаГослинга #gosling_table', 'http://i1.sndcdn.com/avatars-000596325675-88wai8-original.jpg', 'Ryan Gosling', 37),
('Ивент Кухня', 'ДЛЯ КОГО ПОДКАСТ ИВЕНТ КУХНЯ?\r\n\r\nПодкаст будет интересен как начинающим, так и опытным организаторам мероприятий, PR и SMM специалистам, продвигающим события и всем, кто хочет разобраться в особенностях рекламирования и организации ивэнтов. \r\n\r\nЧТО ТАКОЕ ИВЕНТ КУХНЯ?\r\n\r\nЭто шоу и блог об организации и продвижении мероприятий в России. Здесь практики событийного бизнеса делятся своими историями и кейсами. От идеи к маркетингу и реализации проекта.\r\n\r\nТолько самые сочные и вкусные примеры реализованных задумок.\r\n\r\nПриятного вам прослушивания.\r\n', 'https://i.podster.fm/img/222455/crop/yes/size/default/222455-2.jpg', 'Илья По', 38),
('Извините, пирожки', 'Подкаст Ивана Калашникова о футбольной культуре.', 'https://cdn.simplecast.com/images/f89f3291-d4da-41d3-beab-5052a2973c77/58737203-7ddd-4ae7-baea-9ca3ceb80c1e/3000x3000/1539869063artwork.jpg?&amp;aid=rss_feed', 'Sports.ru', 39);
